import Taro from '@tarojs/api';
/**
 * 使手机发生较短时间的振动（15 ms）(harmony平台暂不支持入参type属性)
 *
 * @canUse vibrateShort
 */
declare const vibrateShort: typeof Taro.vibrateShort;
/**
 * 使手机发生较长时间的振动（400 ms)
 *
 * @canUse vibrateLong
 */
declare const vibrateLong: typeof Taro.vibrateLong;
export { vibrateShort, vibrateLong };
